var BootParser = require('../../lib/bootparser');
var Path = require("path");

BootParser.build({

	// location of modules.
	basePath: Path.resolve(__dirname, "modules"),

	// The path to build
	path: Path.resolve(__dirname, 'input/js/include-js.js'),

	// The output path
	js: Path.resolve(__dirname, 'output/output.js'),
	css: Path.resolve(__dirname, 'output/output.css'),
	html: Path.resolve(__dirname, 'output/output.html'),
	images: Path.resolve(__dirname, 'output/images/'),

	// other options for builders.
	jsOptions: {
		"beautify": true,
		"comments": true
	}
});


